import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { bookingsAPI } from '../services/api';
import './BookingPage.css';

const BookingPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const movie = location.state?.movie;

  const [selectedLocation, setSelectedLocation] = useState('');
  const [seats, setSeats] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Redirect if no movie selected
    if (!movie) {
      navigate('/movies');
    }
  }, [movie, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (!selectedLocation) {
      setError('Please select a location');
      return;
    }

    const seatsNum = parseInt(seats);
    if (!seats || seatsNum <= 0) {
      setError('Please enter a valid number of seats (minimum 1)');
      return;
    }

    if (seatsNum > 50) {
      setError('Maximum 50 seats can be booked at once');
      return;
    }

    try {
      setLoading(true);
      const response = await bookingsAPI.createBooking(
        movie._id,
        selectedLocation,
        seatsNum
      );

      // Navigate to confirmation page with booking details
      navigate('/confirmation', {
        state: {
          booking: response.data
        }
      });
    } catch (err) {
      setError(err.message || 'Failed to create booking');
    } finally {
      setLoading(false);
    }
  };

  if (!movie) {
    return null;
  }

  return (
    <div className="booking-container">
      <div className="booking-card">
        <h1>Book Tickets</h1>

        <div className="movie-details">
          <h2>{movie.title}</h2>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="location">Select Location</label>
            <select
              id="location"
              value={selectedLocation}
              onChange={(e) => setSelectedLocation(e.target.value)}
              className="form-select"
              required
            >
              <option value="">Choose a location</option>
              {movie.locations.map((loc, index) => (
                <option key={index} value={loc}>
                  {loc}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="seats">Number of Seats</label>
            <input
              type="number"
              id="seats"
              value={seats}
              onChange={(e) => setSeats(e.target.value)}
              min="1"
              max="50"
              className="form-input"
              placeholder="Enter number of seats (1-50)"
              required
            />
            <small className="form-hint">Minimum: 1, Maximum: 50</small>
          </div>

          <div className="form-actions">
            <button
              type="button"
              onClick={() => navigate('/movies')}
              className="cancel-btn"
            >
              Cancel
            </button>
            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? 'Processing...' : 'Confirm Booking'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BookingPage;
